package com.direction.common.utils.cache;

// ~ File Information
// ====================================================================================================================

public class CacheVO {

	// ~ Static Fields
	// ==================================================================================================================

	// ~ Fields
	// ==================================================================================================================

	private String key;

	private String cacheType;

	private String cacheTypeName;

	private String data;

	// ~ Constructors
	// ==================================================================================================================

	public CacheVO() {

	}

	public CacheVO(String key, String cacheType) {

		this.key = key;
		this.cacheType = cacheType;
		this.cacheTypeName = cacheType;
	}

	public CacheVO(String key, String cacheType, String data) {

		this.key = key;
		this.cacheType = cacheType;
		this.cacheTypeName = cacheType;
		this.data = data;
	}

	// ~ Methods
	// ==================================================================================================================

	public String getKey() {

		return key;
	}

	public void setKey(String key) {

		this.key = key;
	}

	public String getCacheType() {

		return cacheType;
	}

	public void setCacheType(String cacheType) {

		this.cacheType = cacheType;
	}

	public String getData() {

		return data;
	}

	public void setData(String data) {

		this.data = data;
	}

	public String getCacheTypeName() {

		return cacheTypeName;
	}

	public void setCacheTypeName(String cacheTypeName) {

		this.cacheTypeName = cacheTypeName;
	}
}
